import ReactPlayer from "react-player";

export const Buttons = (props) => {
  const { content } = props;

  state = {
    isActive:false
 }

 handleShow = ()=>{
     this.setState({
         isActive: true
     })
 }

 handleHide = () =>{
     this.setState({
         isActive: false
     })
 }


  return (
    <div className="buttonContainer">
      {content.map((item) => (
        <div className="buttonSpace">
          <>
            <button onClick={this.handleShow} id="btn">{item.title}</button>
            <ReactPlayer url={item.embed} />
          </>
        </div>
      ))}
    </div>
  );
};

export default Buttons;
